var confirmDeletion = function() {
	if(window.confirm('Are you sure you want to delete this record?')) {
		return true;
	} else {
		return false;
	}
};

$(function () {
  $('[data-toggle="tooltip"]').tooltip()
});

var keywordCount = 1;

$("#addKeywordBtn").on('click', function(){
	$("#keyword_container_" + keywordCount).after(function(){

		keywordCount++;

		var html = '';

		html += '<div class="form-group col-md-8" id="keyword_container_'+ keywordCount +'">';
		html += '<label>Keyword #'+ keywordCount +':</label>';
		html += '<input type="text" name="campaign_keyword_'+ keywordCount +'" value="" class="form-control" />';
		html += '</div>';

		return html;
	});

	scrollToElement('addKeywordBtn');

});


var scrollToElement = function(id) {
	$('html, body').animate({
	    scrollTop: $("#" + id).offset().top
	}, 2000);
	
};

